mapper.update:
UPDATE board
SET content = #{content}
WHERE board_id = #{boardId}

mapper.select:
SELECT count(*)
FROM board_heart
WHERE user_id = #{userId} AND board_id = #{boardId}

mapper.update:
UPDATE board b
JOIN (SELECT heart FROM board WHERE board_id = #{boardId}) AS sub
ON b.board_id = #{boardId}
SET b.heart = sub.heart + 1
WHERE b.board_id = #{boardId}

UPDATE board b
JOIN (SELECT heart FROM board WHERE board_id = #{boardId}) AS sub
ON b.board_id = #{boardId}
SET b.heart = sub.heart - 1
WHERE b.board_id = #{boardId}

mapper.delete:
DELETE
FROM board_heart
WHERE user_id = #{userId} AND board_id = #{boardId}
